// ---------- State ----------
let cart = JSON.parse(localStorage.getItem("cart")) || [];
let wishlist = JSON.parse(localStorage.getItem("wishlist")) || [];

// ---------- Element References ----------
const productList = document.getElementById("productList");
const cartList = document.getElementById("cartList");
const wishlistList = document.getElementById("wishlist");
const cartSubtotal = document.getElementById("cartSubtotal");
const cartTotal = document.getElementById("cartTotal");
const cartItemsCount = document.getElementById("cartItemsCount");
const wishlistCount = document.getElementById("wishlistCount");
const toastContainer = document.getElementById("toastContainer");

const searchInput = document.getElementById("searchInput");
const sortOption = document.getElementById("sortOption");
const minPrice = document.getElementById("minPrice");
const maxPrice = document.getElementById("maxPrice");
const applyFilters = document.getElementById("applyFilters");
const clearFilters = document.getElementById("clearFilters");
const themeToggle = document.getElementById("themeToggle");

// ---------- Utility ----------
function saveData() {
  localStorage.setItem("cart", JSON.stringify(cart));
  localStorage.setItem("wishlist", JSON.stringify(wishlist));
}

function showToast(msg, type = "success") {
  const toast = document.createElement("div");
  toast.className = `toast ${type}`;
  toast.textContent = msg;
  toastContainer.appendChild(toast);

  setTimeout(() => {
    toast.style.opacity = "0";
    setTimeout(() => toast.remove(), 300);
  }, 2000);
}

function updateCounts() {
  cartItemsCount.textContent = `${cart.length} items`;
  wishlistCount.textContent = wishlist.length;
  const total = cart.reduce((sum, item) => sum + item.price * item.qty, 0);
  cartSubtotal.textContent = total;
  cartTotal.textContent = total;
  document.getElementById("itemCountBadge").textContent = `(${cart.length})`;
}

// ---------- Render Products ----------
function renderProducts(list = products) {
  productList.innerHTML = "";
  list.forEach((p) => {
    const div = document.createElement("div");
    div.className = "product-card";
    div.innerHTML = `
      <img src="${p.img}" alt="${p.name}" />
      <h4>${p.name}</h4>
      <p class="price">â‚¹${p.price}</p>
      <div class="actions">
        <button class="btn small" onclick="addToCart(${p.id})">ðŸ›’</button>
        <button class="btn outline small" onclick="addToWishlist(${p.id})">ðŸ’–</button>
        <button class="btn small" onclick="openModal(${p.id})">ðŸ‘</button>
      </div>
    `;
    productList.appendChild(div);
  });
}

// ---------- Render Cart ----------
function renderCart() {
  cartList.innerHTML = "";
  cart.forEach((item) => {
    const li = document.createElement("li");
    li.className = "list-item";
    li.innerHTML = `
      <span>${item.name}</span>
      <div class="qty-controls">
        <button onclick="changeQty(${item.id}, -1)">âˆ’</button>
        <span>${item.qty}</span>
        <button onclick="changeQty(${item.id}, 1)">+</button>
      </div>
      <span>â‚¹${item.price * item.qty}</span>
      <button onclick="moveToWishlist(${item.id})" class="btn small outline">ðŸ’–</button>
      <button onclick="removeFromCart(${item.id})" class="btn small danger">âœ–</button>
    `;
    cartList.appendChild(li);
  });
  updateCounts();
}

// ---------- Render Wishlist ----------
function renderWishlist() {
  wishlistList.innerHTML = "";
  wishlist.forEach((item) => {
    const li = document.createElement("li");
    li.className = "list-item";
    li.innerHTML = `
      <span>${item.name}</span>
      <button onclick="moveToCart(${item.id})" class="btn small">ðŸ›’</button>
      <button onclick="removeFromWishlist(${item.id})" class="btn small danger">âœ–</button>
    `;
    wishlistList.appendChild(li);
  });
  updateCounts();
}

// Handle Clear Wishlist Button
const clearWishlistBtn = document.getElementById("clearWishlistBtn");

clearWishlistBtn.addEventListener("click", () => {
  wishlist = []; // clear wishlist array
  renderWishlist(); // re-render empty wishlist
  saveWishlist(); // if you have persistence
  showToast("Wishlist cleared! ðŸ’–"); // optional toast message
});

// ---------- Actions ----------
function addToCart(id, quantity = 1) {
  const prod = products.find((p) => p.id === id);
  const existing = cart.find((i) => i.id === id);

  if (existing) {
    existing.qty += quantity;
  } else {
    cart.push({ ...prod, qty: quantity });
  }

  saveData();
  renderCart();
  showToast(`${quantity} Ã— ${prod.name} added to cart`);
}


function addToWishlist(id) {
  const prod = products.find((p) => p.id === id);
  if (!wishlist.find((i) => i.id === id)) {
    wishlist.push(prod);
    saveData();
    renderWishlist();
    showToast(`${prod.name} added to wishlist`);
  } else showToast("Already in wishlist", "error");
}

function removeFromCart(id) {
  cart = cart.filter((i) => i.id !== id);
  saveData();
  renderCart();
  showToast("Removed from cart", "error");
}

function removeFromWishlist(id) {
  wishlist = wishlist.filter((i) => i.id !== id);
  saveData();
  renderWishlist();
  showToast("Removed from wishlist", "error");
}

function changeQty(id, delta) {
  const item = cart.find((i) => i.id === id);
  if (!item) return;
  item.qty += delta;
  if (item.qty <= 0) removeFromCart(id);
  saveData();
  renderCart();
}

function moveToWishlist(id) {
  const item = cart.find((i) => i.id === id);
  removeFromCart(id);
  if (!wishlist.find((w) => w.id === id)) wishlist.push(item);
  saveData();
  renderWishlist();
  showToast("Moved to wishlist");
}

function moveToCart(id) {
  const item = wishlist.find((i) => i.id === id);
  removeFromWishlist(id);
  addToCart(id);
  showToast("Moved to cart");
}

// ---------- Checkout ----------
document.getElementById("checkoutBtn").addEventListener("click", () => {
  if (cart.length === 0) return showToast("Cart is empty!", "error");
  showToast("Checkout successful!");
  cart = [];
  saveData();
  renderCart();
});

// ---------- Search, Filter & Sort ----------
function applySearchFilterSort() {
  let filtered = [...products];

  const searchVal = searchInput.value.toLowerCase();
  if (searchVal)
    filtered = filtered.filter((p) => p.name.toLowerCase().includes(searchVal));

  const min = parseFloat(minPrice.value) || 0;
  const max = parseFloat(maxPrice.value) || Infinity;
  filtered = filtered.filter((p) => p.price >= min && p.price <= max);

  const sortVal = sortOption.value;
  if (sortVal === "priceLow")
    filtered.sort((a, b) => a.price - b.price);
  else if (sortVal === "priceHigh")
    filtered.sort((a, b) => b.price - a.price);
  else if (sortVal === "name")
    filtered.sort((a, b) => a.name.localeCompare(b.name));

  renderProducts(filtered);
}

searchInput.addEventListener("input", applySearchFilterSort);
applyFilters.addEventListener("click", applySearchFilterSort);
clearFilters.addEventListener("click", () => {
  searchInput.value = "";
  minPrice.value = "";
  maxPrice.value = "";
  sortOption.value = "";
  renderProducts();
});

// ---------- Modal (Quick View) ----------
const modal = document.getElementById("productModal");
const modalBackdrop = document.getElementById("modalBackdrop");
const closeModalBtn = document.getElementById("closeModal");

function openModal(id) {
  const prod = products.find((p) => p.id === id);
  document.getElementById("modalImage").src = prod.img;
  document.getElementById("modalTitle").textContent = prod.name;
  document.getElementById("modalDesc").textContent = prod.desc;
  document.getElementById("modalPrice").textContent = prod.price;
  document.getElementById("modalQty").value = 1;
  document.getElementById("modalAddToCart").onclick = () => {
  const qty = parseInt(document.getElementById("modalQty").value) || 1;
  addToCart(id, qty);
  closeModal();
};

  document.getElementById("modalAddToWishlist").onclick = () => {
    addToWishlist(id);
    closeModal();
  };
  modal.classList.remove("hidden");
}

function closeModal() {
  modal.classList.add("hidden");
}

modalBackdrop.addEventListener("click", closeModal);
closeModalBtn.addEventListener("click", closeModal);

